    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
String name,roll,email,date,passa,passb;
try{
roll=txt1.getText();
name=txt2.getText();
email=txt3.getText();
passa=String.valueOf(pass1.getPassword());
passb=String.valueOf(pass2.getPassword());
date=txt4.getText();
Class.forName("oracle.jdbc.driver.OracleDriver");
Connection conn=DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:xe","system","123");
PreparedStatement pr=conn.prepareStatement("SELECT * FROM stu_data WHERE roll=?");
pr.setString(1,roll);
ResultSet rs=pr.executeQuery();
if(rs.next())
{
JOptionPane.showMessageDialog(null,"You are allready Registred");
}
else
{
    if(passa.equals(passb))
    {
        PreparedStatement pre=conn.prepareStatement("insert  into stu_data values(?,?,?,?,?)");

pre.setString(1,roll);
pre.setString(2,name);
pre.setString(3,email);
pre.setString(4,passa.toString());
pre.setString(5,date);
pre.executeQuery();
JOptionPane.showMessageDialog(null,"Thank You,You are now Registred !");
    }
    else
    {
        JOptionPane.showMessageDialog(null,"Password does not match !");
    }
}
}
catch(Exception e){
JOptionPane.showMessageDialog(null,e);}

// TODO add your handling code here:
    }//GEN-LAST:event_jButton1ActionPerformed

