    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
String roll;
try{
roll=txt1.getText();
Class.forName("oracle.jdbc.driver.OracleDriver");
Connection conn=DriverManager.getConnection("jdbc:oracle:thin:@localhost:1521:xe","system","123");
Statement pr=conn.createStatement();
pr.executeUpdate("DELETE FROM stu_data WHERE roll="+roll);
JOptionPane.showMessageDialog(null, "Delete data successfully");
}
catch(Exception exx)// TODO add your handling code here:
{
    JOptionPane.showMessageDialog(null, exx);
}
    }//GEN-LAST:event_jButton4ActionPerformed

